 echoRainbow()
{
    if [ "type lolcat" ]; then
        echo "$@" | lolcat
    else
        echo "$@"
    fi
}
 echoReset()
{
    echo "$@" | tr -d '[:cntrl:]' | sed -E "s/\[((;)?[0-9]{1,3}){0,3}m//g"
}
